mapper.select:
SELECT

FROM t_sys_menu a
WHERE a.id = #{id}

SELECT

FROM t_sys_menu a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.type = #{type}


	AND a.is_show = #{isShow}

ORDER BY a.sort

SELECT

FROM t_sys_menu a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.parent_id = #{parentId}
ORDER BY a.sort

mapper.update:
UPDATE t_sys_menu SET 
	parent_ids = CAST(string_to_array(#{parentIds}, ',') AS INTEGER[])
WHERE id = #{id}

UPDATE t_sys_menu SET 
	sort = #{sort}
WHERE id = #{id}

UPDATE t_sys_menu SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids @> CAST(ARRAY[#{id}] AS INTEGER[])
